import React from "react"

class MemeGenerator extends React.Component {
    constructor(){
        super()
        this.state = {
            topText: "",
            bottomText: "",
            randomImg: "http://i.imgflip.com/1bij.jpg",
            memeimgs: []
        }
    }

    componentDidMount() {
        fetch("https://api.imgflip.com/get_memes")
        .then(response => response.json())
        .then(response =>  {
            const {mem} = response.data
            this.setState({memeimgs:mem})


        })
    }
    render()
    {
        return(
            <div>
                <form className="meme-form">
                <input type="text" name="Toptext" placeholder="Toptext" value={this.state.topText} />
                <input type="text" name="Bottomtext" placeholder="Bottomtext"value={this.state.bottomText} />
                
                 </form>
            </div>
        )

    }
}




export default MemeGenerator
